import Articulo from "./Articulo"
import React, { Component } from 'react'
import axios from 'axios'
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';


function NestedGrid() {
  const gridStyle = artGrid();

  function FormRow() {
    return (
      <React.Fragment>
        <Grid item xs={4}>
          <Paper className={gridStyle.paper}>item</Paper>
        </Grid>
        <Grid item xs={4}>
          <Paper className={gridStyle.paper}>item</Paper>
        </Grid>
        <Grid item xs={4}>
          <Paper className={gridStyle.paper}>item</Paper>
        </Grid>
      </React.Fragment>
    );
  }

  return (
    <div className={classes.root}>
      <Grid container spacing={1}>
        <Grid container item xs={12} spacing={3}>
          <FormRow />
        </Grid>
        <Grid container item xs={12} spacing={3}>
          <FormRow />
        </Grid>
        <Grid container item xs={12} spacing={3}>
          <FormRow />
        </Grid>
      </Grid>
    </div>
  );
}


export default class Articulos extends Component {

    
    useStyles = makeStyles({
        root: {
            flexGrow: 1,
          },
        card: {
            padding: theme.spacing(1),
            textAlign: 'center',
            color: theme.palette.text.secondary,
            maxWidth: 345,
        },
        media: {
            height: 160,
        },
      });

    state={
        articulos: []
      }
    componentWillMount()
    {
        axios.get('https://api.myjson.com/bins/s80td')
        .then(res=>this.setState({articulos:res.data}))
    }

    render() {
        return this.state.articulos.map((articulo)=>(
            <React.Fragment>
                <Articulo key={articulo.id} articulo={articulo} ver={this.props.ver} estilo={this.useStyles}/>
            </React.Fragment>
            
          ))
    }
}
