import React, { Component } from 'react'
import Articulo from "../pages/Articulo"
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid'

export default class FormRow extends Component {
    useStyles = makeStyles({
        card: {
            padding: '8px',
            textAlign: 'center',
            maxWidth: 345,
        },
        media: {
            height: 160,
        },
      });

    articule={
        id: 1000,
        titulo: "Error en formrow",
        descripcion: "No pasaron 3 articulos",
        url: "",
        image:""
      }
    articles = this.props.articulos
    render() {
        if(this.articles.length===6){
            return (
            this.articles.map((articulo)=>(
              <React.Fragment>
                <Grid container spacing={1}></Grid>
                  <Grid container item xs={12} spacing={3}>
                  <Articulo articulo={articulo} ver={articulo.ver} estilo={this.useStyles}/>
                </Grid>
                
              </React.Fragment>)
          ))} else {
            var articulo=this.articule;
            return(
              <React.Fragment>
                <Grid container spacing={1}>
                  <Grid container item xs={12} spacing={3}>
                  <Articulo articulo={articulo} ver={articulo.ver} estilo={this.useStyles}/>
                  </Grid>
                </Grid>
              </React.Fragment>
            )
          }
    }
}
