import Articulo from "./Articulo"
import React, { Component } from 'react'
import axios from 'axios'
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';



function NestedGrid(articulos) {
    function FormRow(articulos) {
      var articule={
        id: 1000,
        titulo: "Error en formrow",
        descripcion: "No pasaron 3 articulos",
        url: "",
        image:""
      }
      var articles = articulos
      if(articles.length===3){
        return (articles.map((articulo)=>(
          <React.Fragment>
            <Grid container spacing={1}>
              <Grid container item xs={12} spacing={3}>
              <Articulo articulo={articulo} ver={articulo.ver} estilo={articles.useStyles}/>
              </Grid>
            </Grid>
              
          </React.Fragment>)
      ))
        
      } else {
        var articulo=articule;
        return(
          <React.Fragment>
            <Grid container spacing={1}>
              <Grid container item xs={12} spacing={3}>
              <Articulo articulo={articulo} ver={articulo.ver} estilo={articles.useStyles}/>
              </Grid>
            </Grid>
          </React.Fragment>
        )
      }
      
    }
  if(articulos.length%3===0){
    //Se define un bucle que itere por cada 3 valores dentro del array de articulos.
    return (
      <div className={articulos.useStyles.root}>
        
            <FormRow articulos={articulos} />
          
      </div>
    );
  } else {
    //Se define articles con un array de 3 que toma los primeros 3 valores de articulos
    var items = []
    const articles=articulos
    for (var i=0;i++;i<3) {
      if(items.length<3){
        items.push(articles[i])
      }
      
    }
    

    return (items.map((articulo)=>(
      <React.Fragment>
          <Grid container spacing={1}>
            <Grid container item xs={12} spacing={3}>
            <Articulo articulo={articulo} ver={articulos.ver} estilo={articulos.useStyles}/>
            </Grid>
          </Grid>
      </React.Fragment>
  )));
  }
  }

export default class Articulos extends Component {

    
    useStyles = makeStyles({
        root: {
            flexGrow: 1,
          },
        card: {
            padding: '8px',
            textAlign: 'center',
            maxWidth: 345,
        },
        media: {
            height: 160,
        },
      });

    state={
        articulos: []
      }

      

      
    componentWillMount()
    {
        axios.get('https://api.myjson.com/bins/s80td')
        .then(res=>this.setState({articulos:res.data}))
    }
    /*
      return this.state.articulos.map((articulo)=>(
            <React.Fragment>
                <Articulo key={articulo.id} articulo={articulo} ver={this.props.ver} estilo={this.useStyles}/>
            </React.Fragment>
        ))
    */
    render() {
        
          return(
            <React.Fragment>
            <NestedGrid articulos={this.state.articulos}/>
            </React.Fragment>
            )
        
    }
}
