import Articulo from "./Articulo"
import React, { Component } from 'react'
import axios from 'axios'
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';





export default class Articulos extends Component {

    
    useStyles = makeStyles({
        root: {
            flexGrow: 1,
          },
        card: {
            padding: '8px',
            textAlign: 'center',
            maxWidth: 345,
        },
        media: {
            height: 160,
        },
      });

    state={
        articulos: []
      }


      NestedGrid() {
        
        articule={
          id: 1000,
          titulo: "Error en formrow",
          descripcion: "No pasaron 3 articulos",
          url: "",
          image:""
        }

        function FormRow(articles) {
          if(articles.length===3){
            articles.map((articulo)=>(
              <React.Fragment>
                  <Articulo articulo={articulo} ver={this.props.ver} estilo={this.useStyles}/>
              </React.Fragment>
          ))
            return (
              <React.Fragment>
                <Grid item xs={4}>
                  <Card className={this.useStyles.card}>item</Card>
                </Grid>
                <Grid item xs={4}>
                  <Card className={this.useStyles.card}>item</Card>
                </Grid>
                <Grid item xs={4}>
                  <Card className={this.useStyles.card}>item</Card>
                </Grid>
              </React.Fragment>
            )
          } else {
            
            return(
              <React.Fragment>
                <Articulo articulo={articulo} ver={this.props.ver} estilo={this.useStyles}/>
              </React.Fragment>
            )
          }
          
        }
        if(this.state.articulos.length%3===0){
          //Se define un bucle que itere por cada 3 valores dentro del array de articulos.
          return (
            <div className={this.useStyles.root}>
              <Grid container spacing={1}>
                <Grid container item xs={12} spacing={3}>
                  <FormRow />
                </Grid>
              </Grid>
            </div>
          );
        } else {
          //Se define articles con un array de 3 que toma los primeros 3 valores de articulos
          const items = []

          for (const [index, value] of articles.entries()) {
            if(items.length<3){
              items.push(value)
            }
            
          }

          return (
            <div className={this.useStyles.root}>
              <Grid container spacing={1}>
                <Grid container item xs={12} spacing={3}>
                  <FormRow articles={items} />
                </Grid>
              </Grid>
            </div>
          );
        }
        
        }
    componentWillMount()
    {
        axios.get('https://api.myjson.com/bins/s80td')
        .then(res=>this.setState({articulos:res.data}))
    }

    render() {
        return this.state.articulos.map((articulo)=>(
            <React.Fragment>
                <Articulo key={articulo.id} articulo={articulo} ver={this.props.ver} estilo={this.useStyles}/>
            </React.Fragment>
        ))
    }
}
