import React, { Component } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Articulo from "./Articulo"
import Grid from '@material-ui/core/Grid'

function FormRow(articulos) {
    const articule={
      id: 1000,
      titulo: "Error en formrow",
      descripcion: "No pasaron 3 articulos",
      url: "",
      image:""
    }
    var articles = articulos
    if(articles.length===3){
      return (articles.map((articulo)=>(
        <React.Fragment>
          <Grid container spacing={1}>
            <Grid container item xs={12} spacing={3}>
            <Articulo articulo={articulo} ver={articulo.ver} estilo={articles.useStyles}/>
            </Grid>
          </Grid>
            
        </React.Fragment>)
    ))
      
    } else {
      var articulo=articule;
      return(
        <React.Fragment>
          <Grid container spacing={1}>
            <Grid container item xs={12} spacing={3}>
            <Articulo articulo={articulo} ver={articulo.ver} estilo={articles.useStyles}/>
            </Grid>
          </Grid>
        </React.Fragment>
      )
    }
    
  }

export default class NestedGrid extends Component {
    useStyles = makeStyles({
        root: {
          flexGrow: 1,
        }
    })
    render() {
        if(this.props.articulos.length%3===0){
            //Se define un bucle que itere por cada 3 valores dentro del array de articulos.
            return (
              <div className={this.useStyles.root}>
                
                    <FormRow articulos={this.props.articulos} />
                  
              </div>
            );
          } else {
            //Se define articles con un array de 3 que toma los primeros 3 valores de articulos
            var items = []
            const articles=this.props.articulos
            for (var i=0;i++;i<3) {
              if(items.length<3){
                items.push(articles[i])
              }
              
            }//EndFor
            return (
            <div className={this.useStyles.root}>
                
              <FormRow articulos={items} />
            
            </div>);
          }
    }
}
