import React, { Component } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import FormRow from "./FormRow"
import Grid from '@material-ui/core/Grid'
import Articulo from "../pages/Articulo"


export default class NestedGrid extends Component {
    constructor(props){
        super(props);
        this.state={
            items:[]
        };

        this.useStyles = makeStyles({
                root: {
                    flexGrow: 1,
                  },
                card: {
                    padding: '8px',
                    textAlign: 'center',
                    maxWidth: 345,
                },
                media: {
                    height: 160,
                },
              })
        

        
    }
    
    
    
    refreshItems=(item)=>{
        
        item=this.state.items.push(item);
        this.setState({items:item});
        this.render();
    }
    

    componentDidMount=()=>{
        var countMods=0
        var item=[]
        for (var i=0;i++;i<this.props.articulos.length) {
            item.push(this.props.articulos[i])
            if(item.length%3===0){
                this.refreshItems(item)
                item=[]
            }
        }

    }
    render() {
        return(
            
            this.state.items.map((articulo)=>(
                <React.Fragment>
                  
                  <Grid container xs={4} spacing={3}>
                  <Grid item xs={4} spacing={3}>
                    <Articulo articulo={articulo} ver={articulo.ver} estilo={this.useStyles}/>
                </Grid>
                  </Grid>
                  
                </React.Fragment>
              )
          )
          ) 
    }
}
