import React, { Component } from 'react'
import Card from '@material-ui/core/Card'
import CardActionArea from '@material-ui/core/CardActionArea'
import CardActions from '@material-ui/core/CardActions'
import CardContent from '@material-ui/core/CardContent'
import CardMedia from '@material-ui/core/CardMedia'
import Typography from '@material-ui/core/Typography'
import Grid from '@material-ui/core/Grid'
import { makeStyles } from '@material-ui/core/styles'
import PropTypes from 'prop-types'
import ArticuloModal from '../components/ArticuloModal'
var style = makeStyles({
    root: {
        flexGrow: 1,
    },
    card: {
        padding: '8px',
        textAlign: 'center',
    },
    media: {
        height: '160px',
    },
    button: {
        label: {
            color: '#3f51b5'
        }

    },
    img:{
        display: 'block',
        position: 'absolute',
        top: '50%',
        left: '50%',
        minHeight: '100%',
        minWidth: '100%'
    },
    titulo:{
        color:'#4495c6',
    },
    primerParrafo:{
        font:'Arial'
    }
})


export default class Articulo extends Component {
    state = {
        style: style,

    }



    render() {
        const { titulo, descripcion, image } = this.props.articulo;
        const stl = style

        return (
            <React.Fragment>
                <Grid item xs={6} md={3} spacing={3} >
                    <Card className={this.state.style.card}>
                        <CardActionArea>
                            <CardMedia
                                component='img'
                                className={this.state.style.media}
                                image={image}
                                title={titulo}
                            />
                            <CardContent>
                                <h2 className={this.state.style.titulo}>
                                    {titulo}
                                </h2>
                                <p className={this.state.style.primerParrafo}>
                                    {descripcion}
                                </p>
                            </CardContent>
                        </CardActionArea>
                        <CardActions>
                            <ArticuloModal titulo={titulo} descripcion={descripcion} image={image}/>
                        </CardActions>
                    </Card>
                </Grid>
            </React.Fragment>
        )
    }

}

//Proptypes
Articulo.propTypes = {
    articulo: PropTypes.object.isRequired,
    estilo: PropTypes.func.isRequired
}
