import React, { Component } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import FormRow from "./FormRow"
import Grid from '@material-ui/core/Grid'

function doRow(items){
    return
    (<Grid container spacing={1}>
        <FormRow articulos={items}/>
    </Grid>
    )
}

export default class NestedGrid extends Component {
    useStyles = makeStyles({
        root: {
          flexGrow: 1,
        }
    })
    render() {
        var countMods
        var items = []
        var articles=this.props.articulos
        for (var i=0;i++;i<articles.length) {
            items.push(articles[i])

                
            if(items.length%3===0){
                <doRow items={items}/>
                items=[]    
            }
                
        }

        return(articles.map((articulo)=>(
            

            <React.Fragment>
                
              <Grid container spacing={1}></Grid>
                <Grid container item xs={12} spacing={3}>
                <Articulo articulo={articulo} ver={articulo.ver} estilo={this.useStyles}/>
              </Grid>
              
            </React.Fragment>
            
            )
        ))
        for (var i=0;i++;i<items.length) {
            items.push(articles[i])

                if(items.length>=3){
                    if(items.length%3===0){
                        countMods++   
                    }
                }
        }
        //Se define un bucle que itere por cada 3 valores dentro del array de articulos.
            return (
              <div className={this.useStyles.root}>
                <Grid container spacing={1}>
                    <FormRow articulos={this.props.articulos} />
                </Grid>
                  
              </div>
            );
    }
}
