import React, { Component } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import FormRow from "./FormRow"
import Grid from '@material-ui/core/Grid'



export default class NestedGrid extends Component {
    constructor(props){
        super(props);
        this.state={
            items:[]
        };

        useStyles = makeStyles({
            root: {
              flexGrow: 1,
            }
        })

        this.onChange= this.onChange.bind(this);
        this.onSubmit= this.onSubmit.bind(this);
    }
    
    
    
    refreshItems=(item)=>{
        var itemadd=(
            <React.Fragment>
              
              <Grid container item xs={4} spacing={3}>
                {FormRow(item)}/>
              </Grid>
              
            </React.Fragment>
        )
        item=this.state.items.push(itemadd);
        this.setState({items:item});
    }
    componentDidUpdate=()=>{
        
            return(item);
    }

    componentDidMount=()=>{
        var countMods=0
        var item=[]
        for (var i=0;i++;i<this.props.articulos.length) {
            item.push(articulos[i])
            if(i%3===0){
                this.refreshItems(item)
            }
        }
        this.setState(this.state.columnas+countMods)

    }
    render() {
        return(
            
            <React.Fragment>
              
              <Grid container item xs={4} spacing={3}>
                {FormRow(this.state.items)}/>
              </Grid>
              
            </React.Fragment>
          ) 
    }
}
