import React, { Component } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import FormRow from "./FormRow"
import Grid from '@material-ui/core/Grid'


export default class NestedGrid extends Component {
    useStyles = makeStyles({
        root: {
          flexGrow: 1,
        }
    })
    render() {
        var countMods
        var items = []
            const articles=this.props.articulos
            for (var i=0;i++;i<this.props.articulos.length) {
              if(items.length<3){
                items.push(articles[i])
            }
              
        }
        if(this.props.articulos.length%3===0){
            //Se define un bucle que itere por cada 3 valores dentro del array de articulos.
            return (
              <div className={this.useStyles.root}>
                <Grid container spacing={1}>
                    <FormRow articulos={this.props.articulos} />
                </Grid>
                  
              </div>
            );
          } else {
            //Se define articles con un array de 3 que toma los primeros 3 valores de articulos
            //EndFor
            return (
                    <div className={this.useStyles.root}>
                        
                        <Grid container spacing={1}>
                            <FormRow articulos={items} />
                        </Grid>
                    
                    </div>
            );
        }
    }
}
