import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Grow from '@material-ui/core/Grow'
import Button from '@material-ui/core/Button'
const useStyles = makeStyles(theme => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius:'16px solid',
    position: 'absolute',
    top:'0',
    left:'0',
    
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    height:'90%',
    width: '90%',
    overflowY:'scroll'
  },
  img:{
    height:'50%%',
    width:'50%'
  },
  titulo:{
    fontFamily:'Georgia',
    display:'block'
  },
  cuerpo:{
    fontFamily:'Arial',
    display:'flex'
  },
  leftCol:{
    width:'50%',
    background:'lightgray',
    display: 'flex',
    alignItems:'left'
  },
  rightCol:{
    display: 'flex',
    alignItems:'right'
  },
  botCol:{
    display:'flex'
  }
}));

export default function ArticuloModal(props) {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const {titulo,descripcion,image}=props

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Button size="small" color="primary" onClick={handleOpen}>
            Ver
        </Button>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 1000,
        }}
      >
        <Grow in={open}>
          <div className={classes.paper}>
            <div className={classes.leftCol}>
              <p><b>Autor:</b> Ejemplo </p><br/>
              <p><b>Fecha:</b> 01 de enero del 01</p>
              
            </div>
            <div className={classes.rightCol}>
              <h1 className={classes.titulo}>{titulo}</h1>
              <p className={classes.cuerpo}>{descripcion}</p>
            </div>
            <div className={classes.botCol}>

            </div>
            
          </div>
        </Grow>
      </Modal>
    </div>
  );
}
